import { useState } from "react";
import axios from "axios";

function App() {

const [data,setData ] = useState({} );
const [location,setLocation ] = useState("" );

const apiKey = "ffcc3b1d6839acddfd18b10b824cdae1";
const apiUrl=  "https://api.openweathermap.org/data/2.5/weather?&units=metric&q=";

 const checkWeather = async () => {
 const response = await fetch(apiUrl + location +'&appid=' + apiKey);
 var APIdata = await response.json();
 setData(APIdata)
 console.log(APIdata);

}


const getData = (event) => {
  if(event.key === 'Enter'){

    checkWeather();
  }
}

  return (
     <div>
      <center>
        <h1 className="font">Weather</h1>
          <div className="search">
            <input 
            type="text"
            id="myText"
            onChange={event => setLocation(event.target.value) }
            onKeyUp={getData}
            placeholder="Search..." 
            />
          </div>
          <br/>
          <h2 className="location">{data.name}</h2>
            <table>
                <tr>
                  <th> <img src="assets/sunny.png" className="slika" /></th>
                    <th><h2 className="temp"></h2>{data.main.temp}</th>
                </tr>
            </table>
            <h2 className="humidity">{data.main.humidity}</h2>
            <h2 clasName="pressure">{data.main.pressure}</h2>
            <h2 className="wind">{data.wind.speed} </h2>

      </center>
     </div>
  );
}

export default App;
