import { useState } from "react";
import axios from "axios";


async function App() {

const [data,setData ] = useState({} );
const [location,setLocation ] = useState(" " );

const apiKey = "ffcc3b1d6839acddfd18b10b824cdae1";
const apiUrl=  "https://api.openweathermap.org/data/2.5/weather?&units=metric&q=";
const response = await fetch(apiUrl + location +'&appid=' + apiKey);
var APIdata = await response.json();

setData(APIdata);

  return (
     <div>
      <center>
        <h1 className="font">Weather</h1>
          <div className="search">
            <input 
            type="text"
            id="myText"
            value={location }
            onChange={event => setLocation(event.target.value)}
            placeholder="Search..." 
            />
          </div>
          <br/>
          <h2 className="location">Beograd</h2>
            <table>
                <tr>
                  <th> <img src="assets/sunny.png" className="slika" alt="img "/></th>
                    <th><h2 className="temp"></h2></th>
                </tr>
            </table>
            <h2 className="humidity"></h2>
            <h2 clasName="pressure"></h2>
            <h2 className="wind"> </h2>

      </center>
     </div>
  );
}

export default App;
